import React, { useState } from "react";

const SignUpForm = () => {
  const [focus, setFocus] = useState({
    name: false,
    username: false,
    password: false,
    confirmPassword: false,
    email: false,
  });

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [usernameTaken, setUsernameTaken] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  // Check if username is available
  const checkUsernameAvailability = async (username) => {
    setUsernameTaken("");
    if (username.length === 0) return;
    const response = await fetch(`http://localhost:5000/api/check-username?username=${username}`);
    const data = await response.json();
    if (data.taken) {
      setUsernameTaken("Username is already taken");
    } else {
      setUsernameTaken("Username is available");
    }
  };

  // Password validation
  const validatePassword = (password) => {
    if (password.length < 6) {
      setPasswordError("Password must be at least 6 characters long.");
    } else {
      setPasswordError("");
    }
  };

  // Email validation
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setEmailError("Invalid email format.");
    } else {
      setEmailError("");
    }
  };

  // Handle input focus
  const handleFocus = (field) => setFocus((prev) => ({ ...prev, [field]: true }));

  // Handle input blur
  const handleBlur = (field, value) => {
    if (!value) setFocus((prev) => ({ ...prev, [field]: false }));
    if (field === "password") validatePassword(value);
    if (field === "email") validateEmail(value);
  };

  // Handle confirm password change
  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
    if (e.target.value !== password) {
      setPasswordError("Passwords do not match");
    } else {
      setPasswordError("");
    }
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    // Additional validation checks
    if (password !== confirmPassword) {
      setLoading(false);
      return alert("Passwords do not match");
    }

    const formData = { username, password, email };

    try {
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (data.message) {
        alert(data.message);
      }

      setLoading(false);
    } catch (error) {
      console.error("Error registering user:", error);
      alert("Error registering user. Please try again.");
      setLoading(false);
    }
  };

  return (
    <div style={{ backgroundImage: "url('lbg.png')", backgroundSize: "cover", backgroundPosition: "center", backgroundAttachment: "fixed", height: "100%", width: "100%" }}>
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center", padding: "20px", minHeight: "100vh" }}>
        <div style={{ background: "rgba(255, 255, 255, 0.7)", backdropFilter: "blur(10px)", WebkitBackdropFilter: "blur(0px)", borderRadius: "10px", padding: "25px", boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)", width: "100%", maxWidth: "400px" }}>
          <form onSubmit={handleSubmit}>
            <h2 style={{ textAlign: "center", color: "#363100", fontSize: "2rem", marginBottom: "20px" }}>CUISINING</h2>

            {/* Email Field */}
            <div style={{ position: "relative", marginBottom: "20px" }}>
              <label style={{ position: "absolute", left: "10px", top: focus.email ? "-10px" : "50%", fontSize: focus.email ? "12px" : "16px", color: "#363100", transition: "all 0.3s ease", transform: focus.email ? "translateY(0)" : "translateY(-50%)", padding: "0 5px" }}>
                Email
              </label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={{ width: "100%", padding: "10px", border: "1px solid transparent", borderRadius: "10px", outline: "none", backgroundColor: "#f8f8f8", transition: "all 0.3s ease", ...(focus.email && { border: "1px solid #C1B857", boxShadow: "0 0 8px rgba(193, 184, 87, 0.5)", backgroundColor: "#fff" }) }}
                onFocus={() => handleFocus("email")}
                onBlur={(e) => handleBlur("email", e.target.value)}
              />
            </div>

            {/* Username Field */}
            <div style={{ position: "relative", marginBottom: "20px" }}>
              <label style={{ position: "absolute", left: "10px", top: focus.username ? "-10px" : "50%", fontSize: focus.username ? "12px" : "16px", color: "#363100", transition: "all 0.3s ease", transform: focus.username ? "translateY(0)" : "translateY(-50%)", padding: "0 5px" }}>
                Username
              </label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                style={{ width: "100%", padding: "10px", border: "1px solid transparent", borderRadius: "10px", outline: "none", backgroundColor: "#f8f8f8", transition: "all 0.3s ease", ...(focus.username && { border: "1px solid #C1B857", boxShadow: "0 0 8px rgba(193, 184, 87, 0.5)", backgroundColor: "#fff" }) }}
                onFocus={() => handleFocus("username")}
                onBlur={(e) => {
                  handleBlur("username", e.target.value);
                  checkUsernameAvailability(e.target.value);
                }}
              />
              {usernameTaken && <div style={{ color: "red" }}>{usernameTaken}</div>}
            </div>

            {/* Password Field */}
            <div style={{ position: "relative", marginBottom: "20px" }}>
              <label style={{ position: "absolute", left: "10px", top: focus.password ? "-10px" : "50%", fontSize: focus.password ? "12px" : "16px", color: "#363100", transition: "all 0.3s ease", transform: focus.password ? "translateY(0)" : "translateY(-50%)", padding: "0 5px" }}>
                Password
              </label>
              <input
                type={showPassword ? "text" : "password"}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={{ width: "100%", padding: "10px", border: "1px solid transparent", borderRadius: "10px", outline: "none", backgroundColor: "#f8f8f8", transition: "all 0.3s ease", ...(focus.password && { border: "1px solid #C1B857", boxShadow: "0 0 8px rgba(193, 184, 87, 0.5)", backgroundColor: "#fff" }) }}
                onFocus={() => handleFocus("password")}
                onBlur={(e) => handleBlur("password", e.target.value)}
              />
              <button type="button" style={{ position: "absolute", right: "10px", top: "50%", transform: "translateY(-50%)", background: "transparent", border: "none", cursor: "pointer" }} onClick={() => setShowPassword(!showPassword)}>
                <img src={showPassword ? "view.png" : "hide.png"} alt={showPassword ? "Hide Password" : "Show Password"} style={{ width: "20px", height: "20px" }} />
              </button>
            </div>
            {passwordError && <div style={{ color: "red", fontSize: "12px", marginTop: "5px" }}>{passwordError}</div>}

            {/* Confirm Password Field */}
            <div style={{ position: "relative", marginBottom: "20px" }}>
              <label style={{ position: "absolute", left: "10px", top: focus.confirmPassword ? "-10px" : "50%", fontSize: focus.confirmPassword ? "12px" : "16px", color: "#363100", transition: "all 0.3s ease", transform: focus.confirmPassword ? "translateY(0)" : "translateY(-50%)", padding: "0 5px" }}>
                Confirm Password
              </label>
              <input
                type={showConfirmPassword ? "text" : "password"}
                value={confirmPassword}
                onChange={handleConfirmPasswordChange}
                style={{ width: "100%", padding: "10px", border: "1px solid transparent", borderRadius: "10px", outline: "none", backgroundColor: "#f8f8f8", transition: "all 0.3s ease", ...(focus.confirmPassword && { border: "1px solid #C1B857", boxShadow: "0 0 8px rgba(193, 184, 87, 0.5)", backgroundColor: "#fff" }) }}
                onFocus={() => handleFocus("confirmPassword")}
                onBlur={(e) => handleBlur("confirmPassword", e.target.value)}
              />
              <button type="button" style={{ position: "absolute", right: "10px", top: "50%", transform: "translateY(-50%)", background: "transparent", border: "none", cursor: "pointer" }} onClick={() => setShowConfirmPassword(!showConfirmPassword)}>
                <img src={showConfirmPassword ? "view.png" : "hide.png"} alt={showConfirmPassword ? "Hide Password" : "Show Password"} style={{ width: "20px", height: "20px" }} />
              </button>
            </div>

            {/* Submit Button */}
            <div style={{ marginTop: "20px", display: "flex", justifyContent: "center" }}>
              <button
                type="submit"
                disabled={loading}
                style={{
                  padding: "10px 20px",
                  backgroundColor: "#C1B857",
                  color: "#fff",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                  fontSize: "16px",
                  transition: "background-color 0.3s ease",
                }}
              >
                {loading ? "Loading..." : "Sign Up"}
              </button>
            </div>
            <div style={{ textAlign: "center", marginBottom: "20px" }}>
              <span style={{ margin: "0 10px" }}>or</span>
              <hr />
              </div>

              <div style={{ display: "flex", justifyContent: "center" }}>
              <a href="#" style={{ marginRight: "10px" }}>
                <img src="facebook.png" alt="Facebook Login" style={{ width: "40px", height: "40px" }} />
              </a>
              <a href="#">
                <img src="google.png" alt="Google Login" style={{ width: "40px", height: "40px" }} />
              </a>
              </div>

              <div style={{ textAlign: "center", marginTop: "20px" }}>
              <p style={{ fontSize: "14px" }}>
                Already Have an Account?{" "}
                <a href="/" style={{ color: "#C1B857" }}>
                  Log In
                </a>
              </p>
              </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignUpForm;
