import React, { useState } from "react";
import '../fw-cuisining.css'; // Relative path from pages folder

const Testing = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <>
      <div className="c-container">
        <h1 className="d-flex b-dashed justify-content-center align-items-center">
          Testing Page
        </h1>

        <div className="d-flex justify-content-center">
          <a href="#" className="cbtn cbtn-primary btn-lg">
            btn-lgasdasdasd
          </a>
        </div>

        {/* Modal */}
        {modalVisible && (
          <div
            className="modal-overlay"
            style={{
              display: 'flex',  // Only show when modalVisible is true
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <div className="modal">
              <div className="modal-header">
                <h5 className="modal-title">Modal Title</h5>
                <button className="modal-close" onClick={closeModal}>
                  Ã—
                </button>
              </div>
              <div className="modal-body">
                <p>
                  function openModal() {'{'}
                  <br />
                  document.querySelector('.modal-overlay').style.display = 'flex';
                  <br />
                  {'}'}
                  <br />
                  <br />
                  function closeModal() {'{'}
                  <br />
                  document.querySelector('.modal-overlay').style.display = 'none';
                  <br />
                  {'}'}
                </p>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={closeModal}>
                  Close
                </button>
                <button className="btn btn-primary">Okay</button>
              </div>
            </div>
          </div>
        )}

        {/* Button to trigger modal */}
        <button className="btn btn-primary" onClick={openModal}>
          modal
        </button>
      </div>
    </>
  );
};

export default Testing;
