import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Use useNavigate instead of useHistory

const SuccessPage = () => {
  const [email, setEmail] = useState("");
  const navigate = useNavigate(); // Hook for navigation

  const styles = {
    background: {
      backgroundImage: "url('lbg.png')", // Replace with your image URL
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundAttachment: "fixed",
      height: "100%",
      width: "100%",
    },
    formWrapper: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      padding: "20px",
      minHeight: "100vh",
    },
    formContainer: {
      background: "rgba(255, 255, 255, 0.7)",
      backdropFilter: "blur(10px)",
      WebkitBackdropFilter: "blur(0px)",
      borderRadius: "10px",
      padding: "25px",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      width: "100%",
      maxWidth: "400px",
      textAlign: "center", // Center the content
    },
    heading: {
      fontSize: "35px",
      color: "#363100",
      fontWeight: "800",
      marginBottom: "20px",
    },
    logo: {
      width: "50px",
      height: "50px",
      marginBottom: "20px", // Space below the logo
    },
    message: {
      fontSize: "16px",
      color: "#363100",
      fontWeight: "300",
      marginBottom: "20px",
    },
    button: {
      padding: "10px 20px",
      borderRadius: "10px",
      backgroundColor: "#C1B857",
      color: "#363100",
      fontWeight: "700",
      cursor: "pointer",
      border: "none",
      fontSize: "16px",
      width: "100%",
    },
    input: {
      padding: "10px",
      borderRadius: "10px",
      border: "1px solid #C1B857",
      width: "100%",
      fontSize: "16px",
      marginBottom: "20px",
    },
  };

  const handleForgotPassword = (e) => {
    e.preventDefault();
    // Here, you would handle the logic for password recovery using the provided email
    console.log("Password recovery requested for email:", email);
    // After submitting, redirect to a confirmation page or back to login
    navigate("/"); // Replace with the actual path after the recovery process
  };

  return (
    <div style={styles.background}>
      <div style={styles.formWrapper}>
        <div style={styles.formContainer}>
          {/* Check mark logo */}
          <img src="cuisining-wordmark.png" alt="Success" style={styles.logo} />

          {/* Heading */}
          <h2 style={styles.heading}>Successful naka log in</h2>

          {/* Success message */}
          <p style={styles.message}>
            Welcome sa luto luto
          </p>

          {/* Forgot Password Form */}
          <form onSubmit={handleForgotPassword}>
            <input
              type="email"
              placeholder="Enter your Gmail"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={styles.input}
              required
            />
            <button type="submit" style={styles.button}>
              Submit for Password Reset
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SuccessPage;
