import 'bootstrap/dist/css/bootstrap.min.css';  
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginForm = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [focus, setFocus] = useState({ email: false, password: false });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle input focus
  const handleFocus = (field) => setFocus((prev) => ({ ...prev, [field]: true }));
  
  // Handle input blur
  const handleBlur = (field, value) => {
    if (!value) setFocus((prev) => ({ ...prev, [field]: false }));
  };

  // Validate email format
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email) ? "" : "Please enter a valid email address.";
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const emailError = validateEmail(formData.email);
    if (emailError) {
      setError(emailError);
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (response.status === 200) {
        localStorage.setItem("authToken", data.token);

        // Fetch profile customization status
        const profileResponse = await fetch("http://localhost:5000/api/profile/profile", {
          headers: { Authorization: `Bearer ${data.token}` },
        });

        const profileData = await profileResponse.json();
        navigate(profileData.isProfileCustomized ? "/home-page" : "/customize-profile");
      } else {
        setError(data.message || "Invalid email or password.");
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div style={styles.background}>
      <div style={styles.formWrapper}>
        <div style={styles.formContainer}>
          <form onSubmit={handleSubmit}>
            <h2 style={styles.heading}>CUISINING</h2>

            {/* Email Input */}
            <div style={styles.inputWrapper}>
              <label style={styles.label(focus.email)}>Email</label>
              <input
                type="email"
                name="email"
                autoFocus
                style={{ ...styles.input, ...(focus.email && styles.inputFocused) }}
                value={formData.email}
                onChange={handleChange}
                onFocus={() => handleFocus("email")}
                onBlur={(e) => handleBlur("email", e.target.value)}
              />
            </div>

            {/* Password Input */}
            <div style={styles.inputWrapper}>
              <label style={styles.label(focus.password)}>Password</label>
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                style={{ ...styles.input, ...(focus.password && styles.inputFocused) }}
                value={formData.password}
                onChange={handleChange}
                onFocus={() => handleFocus("password")}
                onBlur={(e) => handleBlur("password", e.target.value)}
              />
              {/* Show/Hide Password Button */}
              <button
                type="button"
                style={styles.showPasswordButton}
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "üëÅÔ∏è" : "üîí"}
              </button>
            </div>

            {/* Error Message */}
            {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}

            <button type="submit" style={styles.button} disabled={!formData.email || !formData.password}>
              Log In
            </button>

            {/* Forgot Password */}
            <a href="/ForgotPass" style={styles.forgotPasswordLink}>Forgot Password?</a>

            {/* OR Divider */}
            <div style={styles.hrContainer}>
              <hr style={styles.hr} />
              <span style={styles.orText}>or</span>
              <hr style={styles.hr} />
            </div>

            {/* Social Login Buttons */}
            <div style={styles.socialButtonsContainer}>
              <img src="facebook.png" alt="Facebook Login" style={styles.socialButtonImg} />
              <img src="google.png" alt="Google Login" style={styles.socialButtonImg} />
            </div>

            <div style={styles.signup}>
              <p style={styles.signupText}>
                Don't Have an Account? <a href="/signup" style={styles.signupLink}>Sign Up</a>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

// Styles
const styles = {
  background: { backgroundImage: "url('lbg.png')", backgroundSize: "cover", height: "100vh" },
  formWrapper: { display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" },
  formContainer: { background: "rgba(255, 255, 255, 0.7)", padding: "25px", borderRadius: "10px", maxWidth: "400px" },
  inputWrapper: { position: "relative", marginBottom: "20px" },
  label: (focused) => ({ position: "absolute", left: "10px", top: focused ? "-10px" : "50%", transition: "0.3s" }),
  input: { width: "100%", padding: "10px", borderRadius: "10px", backgroundColor: "#f8f8f8" },
  inputFocused: { border: "1px solid #C1B857", backgroundColor: "#fff" },
  button: { width: "100%", padding: "10px", borderRadius: "10px", backgroundColor: "#C1B857", color: "#363100" },
  hrContainer: { display: "flex", alignItems: "center", justifyContent: "center", margin: "20px 0" },
  hr: { flex: 1, height: "1px", backgroundColor: "#000000" },
  orText: { margin: "0 10px", fontSize: "16px", fontWeight: "300" },
  socialButtonsContainer: { display: "flex", justifyContent: "center", gap: "20px" },
  socialButtonImg: { width: "45px", height: "auto", borderRadius: "10px", cursor: "pointer" },
  signup: { justifyContent: "center", display: "flex", marginTop: "20px" },
  signupText: { fontSize: "14px", fontWeight: "200" },
  signupLink: { textDecoration: "none", fontWeight: "700", color: "#363100" },
  heading: { textAlign: "center", fontSize: "35px", fontWeight: "800" },
  showPasswordButton: { position: "absolute", right: "10px", top: "50%", transform: "translateY(-50%)", background: "none", border: "none", cursor: "pointer" },
  forgotPasswordLink: { display: "block", textAlign: "center", fontSize: "14px", marginTop: "10px", fontWeight: "500" },
};

export default LoginForm;
