import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const SignUpForm = () => {
  const navigate = useNavigate();

  const [focus, setFocus] = useState({
    email: false,
    fullname: false,
    password: false,
  });

  const [showPassword, setShowPassword] = useState(false);
  const [passwordError, setPasswordError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [fullnameError, setFullnameError] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [codeSent, setCodeSent] = useState(false);
  const [verificationCode, setVerificationCode] = useState("");
  const [userEnteredCode, setUserEnteredCode] = useState("");

  const [formData, setFormData] = useState({
    email: "",
    fullname: "",
    password: "",
  });

  // Email validation
  const validateEmail = (email) => {
    if (!email) {
      return "Email is required.";
    }
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      return "Please enter a valid email address.";
    }
    return "";
  };

  // Full Name validation
  const validateFullname = (fullname) => {
    if (!fullname) {
      return "Full Name is required.";
    }
    if (fullname.length < 4) {
      return "Full Name must be at least 4 characters long.";
    }
    return "";
  };

  // Password validation
  const validatePassword = (password) => {
    const passwordLength = password.length >= 6;
    const hasUpperCase = /[A-Z]/.test(password);
    const hasLowerCase = /[a-z]/.test(password);
    const hasNumbers = /\d/.test(password);
    const hasSpecialChars = /[!@#$%^&*(),.?":{}|<>]/.test(password);

    if (!passwordLength) {
      return "Password must be at least 6 characters long.";
    } else if (!hasUpperCase || !hasLowerCase) {
      return "Password must contain both uppercase and lowercase letters.";
    } else if (!hasNumbers) {
      return "Password must contain at least one number.";
    } else if (!hasSpecialChars) {
      return "Password must contain at least one special character.";
    }

    return "";
  };

  // Handle input focus and blur
  const handleFocus = (field) => setFocus((prev) => ({ ...prev, [field]: true }));
  const handleBlur = (field, value) => {
    if (!value) setFocus((prev) => ({ ...prev, [field]: false }));
    if (field === "email") setEmailError(validateEmail(value));
    if (field === "fullname") setFullnameError(validateFullname(value));
    if (field === "password") setPasswordError(validatePassword(value));
  };

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Generate and send verification code (API integration)
  const sendVerificationCode = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/otp/send-otp", {
        email: formData.email,
      });
      setVerificationCode(response.data.code); // Store the actual code in the backend
      setCodeSent(true);
      alert(`Verification code sent to ${formData.email}.`);
    } catch (error) {
      setErrorMessage("Error sending verification code.");
    }
  };

  // Verify the OTP
  const verifyOtp = async () => {
    if (userEnteredCode !== verificationCode) {
      setErrorMessage("Invalid verification code.");
      return;
    }

    setIsSubmitting(true);
    setErrorMessage("");

    try {
      const response = await axios.post("http://localhost:5000/api/auth/signup", {
        email: formData.email,
        fName: formData.fullname,
        password: formData.password,
      });

      console.log("Signup successful", response.data);
      navigate("/DoneRegister");
    } catch (error) {
      console.error("Signup error:", error);
      setErrorMessage(error.response?.data?.message || "An error occurred during signup.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const styles = {
    background: {
      backgroundImage: "url('lbg.png')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundAttachment: "fixed",
      height: "100%",
      width: "100%",
    },
    formWrapper: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      padding: "20px",
      minHeight: "100vh",
    },
    formContainer: {
      background: "rgba(255, 255, 255, 0.7)",
      backdropFilter: "blur(10px)",
      WebkitBackdropFilter: "blur(0px)",
      borderRadius: "10px",
      padding: "25px",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      width: "100%",
      maxWidth: "400px",
    },
    inputWrapper: {
      position: "relative",
      marginBottom: "20px",
    },
    label: (focused) => ({
      position: "absolute",
      left: "10px",
      top: focused ? "-10px" : "50%",
      fontSize: focused ? "12px" : "16px",
      color: "#363100",
      transition: "all 0.3s ease",
      transform: focused ? "translateY(0)" : "translateY(-50%)",
      padding: "0 5px",
    }),
    input: {
      width: "100%",
      padding: "10px",
      border: "1px solid transparent",
      borderRadius: "10px",
      outline: "none",
      backgroundColor: "#f8f8f8",
      transition: "all 0.3s ease",
    },
    inputFocused: {
      border: "1px solid #C1B857",
      boxShadow: "0 0 8px rgba(193, 184, 87, 0.5)",
      backgroundColor: "#fff",
    },
    button: {
      width: "100%",
      padding: "10px",
      margin: "10px 0",
      border: "1px solid #ccc",
      borderRadius: "10px",
      backgroundColor: "#C1B857",
      color: "#363100",
      cursor: "pointer",
      fontWeight: "700",
    },
    sendCodeButton: {
      padding: "10px",
      border: "1px solid #ccc",
      borderRadius: "10px",
      backgroundColor: "#C1B857",
      color: "#363100",
      cursor: "pointer",
      fontWeight: "700",
      marginLeft: "10px",
    },
    codeContainer: {
      display: "flex",
      alignItems: "center",
      marginBottom: "20px",
    },
    codeInput: {
      flex: 1,
      padding: "10px",
      border: "1px solid transparent",
      borderRadius: "10px",
      outline: "none",
      backgroundColor: "#f8f8f8",
      transition: "all 0.3s ease",
    },
  };

  return (
    <div style={styles.background}>
      <div style={styles.formWrapper}>
        <div style={styles.formContainer}>
          <form onSubmit={(e) => { e.preventDefault(); verifyOtp(); }}>
            <h2 style={styles.heading}>CUISINING</h2>

            {/* Email Input */}
            <div style={styles.inputWrapper}>
              <label htmlFor="email" style={styles.label(focus.email)}>Email</label>
              <input
                id="email"
                type="email"
                name="email"
                value={formData.email}
                style={{
                  ...styles.input,
                  ...(focus.email && styles.inputFocused),
                }}
                onFocus={() => handleFocus("email")}
                onBlur={(e) => handleBlur("email", e.target.value)}
                onChange={handleChange}
              />
              {emailError && <div style={styles.emailError}>{emailError}</div>}
            </div>

            {/* Full Name Input */}
            <div style={styles.inputWrapper}>
              <label htmlFor="fullname" style={styles.label(focus.fullname)}>Full Name</label>
              <input
                id="fullname"
                type="text"
                name="fullname"
                value={formData.fullname}
                style={{
                  ...styles.input,
                  ...(focus.fullname && styles.inputFocused),
                }}
                onFocus={() => handleFocus("fullname")}
                onBlur={(e) => handleBlur("fullname", e.target.value)}
                onChange={handleChange}
              />
              {fullnameError && <div style={styles.usernameError}>{fullnameError}</div>}
            </div>

            {/* Password Input */}
            <div style={styles.inputWrapper}>
              <label htmlFor="password" style={styles.label(focus.password)}>Password</label>
              <input
                id="password"
                type={showPassword ? "text" : "password"}
                name="password"
                value={formData.password}
                style={{
                  ...styles.input,
                  ...(focus.password && styles.inputFocused),
                }}
                onFocus={() => handleFocus("password")}
                onBlur={(e) => handleBlur("password", e.target.value)}
                onChange={handleChange}
              />
              {passwordError && <div style={styles.passwordError}>{passwordError}</div>}
            </div>

            {/* Send OTP Button */}
            <div style={styles.codeContainer}>
              <button
                type="button"
                style={styles.sendCodeButton}
                onClick={sendVerificationCode}
                disabled={codeSent || isSubmitting}
              >
                Send OTP
              </button>
            </div>

            {/* OTP Input */}
            {codeSent && (
              <div style={styles.codeContainer}>
                <input
                  type="text"
                  name="otp"
                  value={userEnteredCode}
                  style={styles.codeInput}
                  onChange={(e) => setUserEnteredCode(e.target.value)}
                  placeholder="Enter OTP"
                  maxLength="6"
                />
              </div>
            )}

            {/* Submit Button */}
            <button type="submit" style={styles.button} disabled={isSubmitting}>
              {isSubmitting ? "Submitting..." : "Sign Up"}
            </button>
            {errorMessage && <div style={{ color: "red" }}>{errorMessage}</div>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignUpForm;
