import React, { useState } from "react";

const SetNewPasswordForm = () => {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [confirmPasswordError, setConfirmPasswordError] = useState("");

  const validatePassword = (value) => {
    // Add your password validation logic here (e.g., length, special characters)
    if (value.length < 8) {
      setPasswordError("Password must be at least 8 characters long.");
    } else {
      setPasswordError("");
    }
  };

  const validateConfirmPassword = (value) => {
    if (value !== password) {
      setConfirmPasswordError("Passwords do not match.");
    } else {
      setConfirmPasswordError("");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!passwordError && !confirmPasswordError && password && confirmPassword) {
      // Proceed with the password update logic
      console.log("Password updated successfully");
    }
  };

  const styles = {
    background: {
      backgroundImage: "url('lbg.png')", // Replace with your image URL
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundAttachment: "fixed",
      height: "100vh",
      width: "100%",
    },
    formWrapper: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      padding: "20px",
      minHeight: "100vh",
    },
    formContainer: {
      background: "rgba(255, 255, 255, 0.7)",
      backdropFilter: "blur(10px)",
      WebkitBackdropFilter: "blur(0px)",
      borderRadius: "10px",
      padding: "25px",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      width: "100%",
      maxWidth: "400px",
    },
    heading: {
      textAlign: "center",
      fontSize: "35px",
      color: "#363100",
      fontWeight: "800",
      lineHeight: "1.2",
    },
    infoText: {
      fontSize: "16px",
      textAlign: "center",
      color: "#363100",
    },
    inputWrapper: {
      marginBottom: "20px",
    },
    input: {
      width: "100%",
      padding: "10px",
      borderRadius: "10px",
      border: "1px solid #ccc",
      marginBottom: "10px",
      fontSize: "16px",
    },
    errorText: {
      color: "red",
      fontSize: "12px",
      textAlign: "center",
    },
    button: {
      width: "100%",
      padding: "10px",
      margin: "20px 0",
      border: "1px solid #ccc",
      borderRadius: "10px",
      backgroundColor: "#C1B857",
      color: "#363100",
      cursor: "pointer",
      fontWeight: "700",
    },
  };

  return (
    <div style={styles.background}>
      <div style={styles.formWrapper}>
        <div style={styles.formContainer}>
          <h2 style={styles.heading}>Set a New Password</h2>
          <p style={styles.infoText}>
            Create a new password. Ensure it differs from previous ones for security.
          </p>
          <form onSubmit={handleSubmit}>
            <div style={styles.inputWrapper}>
              <input
                type="password"
                placeholder="New Password"
                value={password}
                onChange={(e) => {
                  setPassword(e.target.value);
                  validatePassword(e.target.value);
                }}
                style={styles.input}
              />
              {passwordError && <p style={styles.errorText}>{passwordError}</p>}
            </div>

            <div style={styles.inputWrapper}>
              <input
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => {
                  setConfirmPassword(e.target.value);
                  validateConfirmPassword(e.target.value);
                }}
                style={styles.input}
              />
              {confirmPasswordError && <p style={styles.errorText}>{confirmPasswordError}</p>}
            </div>

            <button type="submit" style={styles.button} disabled={passwordError || confirmPasswordError}>
              Update Password
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SetNewPasswordForm;
