import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const LoginForm = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [focus, setFocus] = useState({ email: false, password: false });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle input focus and blur
  const handleFocus = (field) => setFocus((prev) => ({ ...prev, [field]: true }));
  const handleBlur = (field, value) => {
    if (!value) setFocus((prev) => ({ ...prev, [field]: false }));
  };

  // Validate email format
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email) ? "" : "Please enter a valid email address.";
  };

  // Extract token from query string
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get("token");
    if (token) {
      localStorage.setItem("authToken", token);
      window.history.replaceState({}, document.title, "/customize-profile"); // Clean up the URL
    }
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const emailError = validateEmail(formData.email);
    if (emailError) {
      setError(emailError);
      return;
    }

    try {
      // Step 1: Log in the user
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      console.log("Login response:", data);

      if (response.status === 200) {
        const token = data.token; // Get token from JSON response
        localStorage.setItem("authToken", token); // Store token in localStorage
        console.log("Token stored in localStorage:", token);

        // Step 2: Fetch profile customization status
        const profileResponse = await fetch("http://localhost:5000/api/profile/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!profileResponse.ok) {
          const errorData = await profileResponse.json();
          console.error("Error fetching profile:", errorData);
          throw new Error("Failed to fetch profile");
        }

        const profileData = await profileResponse.json();
        console.log("Profile response:", profileData);

        // Step 3: Redirect based on profile customization status
        if (profileData.isProfileCustomized) {
          navigate("/home-page");
        } else {
          navigate("/customize-profile");
        }
      } else {
        setError(data.message || "Invalid email or password.");
      }
    } catch (err) {
      console.error("Login error:", err);
      setError("An error occurred. Please try again.");
    }
  };

  // Styles
  const styles = {
    background: {
      backgroundImage: "url('lbg.png')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundAttachment: "fixed",
      height: "100%",
      width: "100%",
    },
    formWrapper: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      padding: "20px",
      minHeight: "100vh",
    },
    formContainer: {
      background: "rgba(255, 255, 255, 0.7)",
      backdropFilter: "blur(10px)",
      WebkitBackdropFilter: "blur(0px)",
      borderRadius: "10px",
      padding: "25px",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      width: "100%",
      maxWidth: "400px",
    },
    inputWrapper: {
      position: "relative",
      marginBottom: "20px",
    },
    label: (focused) => ({
      position: "absolute",
      left: "10px",
      top: focused ? "-10px" : "50%",
      fontSize: focused ? "12px" : "16px",
      color: "#363100",
      transition: "all 0.3s ease",
      transform: focused ? "translateY(0)" : "translateY(-50%)",
      padding: "0 5px",
    }),
    input: {
      width: "100%",
      padding: "10px",
      border: "1px solid transparent",
      borderRadius: "10px",
      outline: "none",
      backgroundColor: "#f8f8f8",
      transition: "all 0.3s ease",
    },
    inputFocused: {
      border: "1px solid #C1B857",
      boxShadow: "0 0 8px rgba(193, 184, 87, 0.5)",
      backgroundColor: "#fff",
    },
    button: {
      width: "100%",
      padding: "10px",
      margin: "10px 0",
      border: "1px solid #ccc",
      borderRadius: "10px",
      backgroundColor: "#C1B857",
      color: "#363100",
      cursor: "pointer",
      fontWeight: "700",
    },
    hrContainer: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      margin: "20px 0",
    },
    hr: {
      flex: 1,
      height: "1px",
      backgroundColor: "#000000",
      border: "none",
    },
    orText: {
      margin: "0 10px",
      fontSize: "16px",
      color: "#363100",
      fontWeight: "300",
    },
    forgotPasswordLink: {
      display: "block",
      textAlign: "center",
      fontSize: "14px",
      color: "#363100",
      textDecoration: "none",
      marginTop: "10px",
      fontWeight: "500",
    },
    showPasswordButton: {
      position: "absolute",
      right: "10px",
      top: "50%",
      transform: "translateY(-50%)",
      background: "transparent",
      border: "none",
      cursor: "pointer",
    },
    signup: {
      justifyContent: "center",
      display: "flex",
      marginTop: "20px",
    },
    signupLink: {
      textDecoration: "none",
      fontWeight: "700",
      color: "#363100",
    },
    heading: {
      textAlign: "center",
      fontSize: "35px",
      color: "#363100",
      fontWeight: "800",
      lineHeight: "1.2",
    },
  };

  return (
    <div style={styles.background}>
      <div style={styles.formWrapper}>
        <div style={styles.formContainer}>
          <h1 style={styles.heading}>CUISINING</h1>

          {/* Email Input */}
          <div style={styles.inputWrapper}>
            <input
              type="email"
              name="email"
              placeholder=" "
              onFocus={() => handleFocus("email")}
              onBlur={(e) => handleBlur("email", e.target.value)}
              onChange={handleChange}
              style={{
                ...styles.input,
                ...(focus.email && styles.inputFocused),
              }}
            />
            <label style={styles.label(focus.email)}>Email</label>
          </div>
          {error && <p style={{ color: "red" }}>{error}</p>}

          {/* Password Input */}
          <div style={styles.inputWrapper}>
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              placeholder=" "
              onFocus={() => handleFocus("password")}
              onBlur={(e) => handleBlur("password", e.target.value)}
              onChange={handleChange}
              style={{
                ...styles.input,
                ...(focus.password && styles.inputFocused),
              }}
            />
            <label style={styles.label(focus.password)}>Password</label>
            <button
              type="button"
              onClick={() => setShowPassword(!showPassword)}
              style={styles.showPasswordButton}
            >
              {showPassword ? "Hide" : "Show"}
            </button>
          </div>

          {/* Forgot Password Link */}
          <a href="/forgot-password" style={styles.forgotPasswordLink}>
            Forgot Password?
          </a>

          {/* Submit Button */}
          <button onClick={handleSubmit} style={styles.button}>
            Log In
          </button>

          {/* OR Divider */}
          <div style={styles.hrContainer}>
            <hr style={styles.hr} />
            <span style={styles.orText}>or</span>
            <hr style={styles.hr} />
          </div>

          {/* Social Login Buttons */}
          <div style={styles.signup}>
            Don't Have an Account?{" "}
            <a href="/signup" style={styles.signupLink}>
              Sign Up
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
